import axios from "axios";

export default {
    //Get a list of categories. 
    getRestaurantByCategories: function() {
        return axios.get("curl -X GET --header 'Accept: application/json' --header 'user-key: 5acad2cbe9a75ee1b16a2b5c97f6e16d' 'https://developers.zomato.com/api/v2.1/categories'")
    },
    //get a list of restaurant by city name
    getRestaurantByCity: function() {
        return axios.get("https://developers.zomato.com/api/v2.1/cities")
    },
    //get by restaurant name
    getRestaurantByRestaurant: function() {
        return axios.get("https://developers.zomato.com/api/v2.1/restaurant")
    },
    //get restaunt reviews
    getRestaurantReview: function() {
        return axios.get("https://developers.zomato.com/api/v2.1/reviews")
    },
    //getRestaurant by Search
    getRestaurantBySearch: function() {
        return axios.get("https://developers.zomato.com/api/v2.1/search")
    },
    //Get Foodie Index, Nightlife Index, Top Cuisines and Best rated restaurants in a given location
    getRestaurantByLocationDetails: function() {
        return axios.get("https://developers.zomato.com/api/v2.1/location_details")
    }
};
